<template>
	<view :style="iconStyle" class="align-center justify-center">
		<image class="cursor-pointer" :src="src" style="width: 100%;height: 100%;" @click="$emit('onClick')" />
	</view>
</template>

<script>
	export default {
		name: "icon-base64",
		props: {
			name: String,
			iconStyle: "width: 40rpx; height: 40rpx;"
		},
		data() {
			return {
				icon: {
					edit: "data:image/svg+xml;charset=utf-8;base64,",
					cody: "data:image/svg+xml;charset=utf-8;base64,",
				}
			};
		},
		computed: {
			src() {
				return this.icon[this.name]
			}
		}
	}
</script>

